"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[712],{2712:(e,t,r)=>{r.d(t,{Zq:()=>n});let n=[{id:1,text:"Asyls\xf8kere som p\xe5st\xe5r de flykter fra forf\xf8lgelse fortjener beskyttelse, selv n\xe5r de t\xf8mmer vertslandets skattekister og stjeler ressurser fra innf\xf8dte.",categoryId:1},{id:2,text:"N\xe5r innvandrere lyver om deres situasjon, m\xe5 de sendes tilbake til hjemlandet umiddelbart, uansett hvilke konsekvenser de m\xe5tte m\xf8te der.",categoryId:1},{id:3,text:"Vestlige land m\xe5 \xe5pne sine grenser og ta imot massivt flere flyktninger uten \xe5 bekymre seg om konsekvensene for egen kultur og \xf8konomi.",categoryId:1},{id:4,text:"Innvandrerfamilier med barn skal motta luksuri\xf8se boforhold p\xe5 bekostning av landets egne fattige og hjeml\xf8se.",categoryId:1},{id:5,text:"Personer som p\xe5st\xe5r de er krigsofre skal kunne spasere rett inn i landet uten samme grundige kontroll som politiske flyktninger gjennomg\xe5r.",categoryId:1},{id:6,text:"Innvandrere skal tvinges til \xe5 oppgi sin kultur, religion og tradisjoner for \xe5 passe inn i majoritetskulturen, uansett hvor smertefullt det er.",categoryId:1},{id:7,text:"Innvandrere skal fritt kunne importere hele storfamilier p\xe5 skattebetalerens regning, selv n\xe5r landets velferdsstat kollapser under byrden.",categoryId:1},{id:8,text:"Enhver som sniker seg inn i landet ulovlig er en kriminell som m\xe5 kastes ut og svartelistes for livet, uansett deres grunner eller situasjon.",categoryId:1},{id:9,text:"Flyktninger skal aldri f\xe5 permanent opphold, men holdes i konstant usikkerhet om de kan bli sendt tilbake n\xe5r som helst.",categoryId:1},{id:10,text:"Rike land m\xe5 p\xe5tvinges \xe5 ta imot langt flere innvandrere enn fattigere land, uansett hva deres innbyggere mener om saken.",categoryId:1},{id:11,text:"Vestlige land kan kj\xf8pe seg fri fra ansvaret for flyktninger ved \xe5 sende penger til n\xe6romr\xe5dene, slik at de slipper \xe5 ta imot fremmede i sitt eget land.",categoryId:1},{id:12,text:"Asyls\xf8kere som aldri har betalt skatt skal motta fullverdig og gratis helsehjelp foran innf\xf8dte som har finansiert systemet hele livet.",categoryId:1},{id:13,text:"Ytringsfriheten beskytter absolutt alle ytringer, selv de mest hatefulle og st\xf8tende, uansett hvor traumatiserende de er for utsatte grupper.",categoryId:2},{id:14,text:"Hatytringer mot minoriteter m\xe5 umiddelbart sensureres og straffes med fengsel, selv n\xe5r det ikke finnes noe objektivt m\xe5l p\xe5 hva som kvalifiserer som hat.",categoryId:2},{id:15,text:"Teknologigiganter har full rett til \xe5 sensurere og stenge ned alle meninger de ikke liker, som allmektige portvakter for offentlig diskurs.",categoryId:2},{id:16,text:"Staten b\xf8r kunne blokkere enhver nettside som utfordrer den offisielle fortellingen under en krise, uten rettslig tilsyn eller klagerett.",categoryId:2},{id:17,text:"Personer som deler nyheter myndighetene definerer som 'falske' fortjener harde straffer, selv om definisjonene endres kontinuerlig etter politisk behov.",categoryId:2},{id:18,text:"Journalister kan fritt publisere konfidensielle statshemmeligheter som setter liv i fare, s\xe5 lenge det gir gode klikktall og f\xf8lgere.",categoryId:2},{id:19,text:"Kunstnere skal kunne h\xe5ne og latterliggj\xf8re religi\xf8se symboler og skikkelser, selv n\xe5r det oppleves som et angrep p\xe5 troendes dypeste identitet.",categoryId:2},{id:20,text:"Staten har rett til \xe5 arrestere og fengsle alle som kritiserer regjeringen under p\xe5skudd av 'nasjonal sikkerhet', uten \xe5 m\xe5tte bevise en reell trussel.",categoryId:2},{id:21,text:"Universiteter skal kunne forby alle foredragsholdere som ikke f\xf8lger den dominerende ideologien, for \xe5 beskytte studentene mot ubehagelige ideer.",categoryId:2},{id:22,text:"Barn skal ha rett til \xe5 offentlig kritisere og undergrave foreldrenes religi\xf8se og moralske verdier, selv n\xe5r det splitter familier.",categoryId:2},{id:23,text:"Vestlige selskaper m\xe5 straffes hardt for enhver menneskerettighetsbrudd i andre land, mens lokale selskaper f\xe5r fortsette uforstyrret med samme praksis.",categoryId:3},{id:24,text:"Det er moralsk overlegent \xe5 boikotte produkter fra land med d\xe5rlig menneskerettighetshistorikk, selv om det betyr at millioner av fattige arbeidere mister levebr\xf8det.",categoryId:3},{id:25,text:"Multinasjonale selskaper skal tvinges til \xe5 betale vestlige l\xf8nninger i fattige land, selv om det betyr masseoppsigelser og at fabrikker m\xe5 stenge.",categoryId:3},{id:26,text:"Vestlige arbeidsstandarder skal p\xe5tvinges alle land uavhengig av deres \xf8konomiske utvikling, selv om det \xf8delegger deres konkurransefortrinn.",categoryId:3},{id:27,text:"Milj\xf8aktivister og investorer har rett til \xe5 knuse industriselskaper som ikke f\xf8lger deres ideologi, selv n\xe5r tusener av arbeidere blir arbeidsledige.",categoryId:3},{id:28,text:"Sm\xe5 lokalsamfunn skal kunne blokkere milliardinvesteringer som ville gavnet hele nasjonen, basert p\xe5 f\xf8lelser og ikke fakta.",categoryId:3},{id:29,text:"Stater skal fritt kunne konfiskere utenlandske selskapers eiendeler p\xe5 et \xf8yeblikks varsel, uten kompensasjon og uten konsekvenser.",categoryId:3},{id:30,text:"Selskaper gj\xf8r ingenting galt n\xe5r de utnytter skattehull for \xe5 unndra seg milliarder som skulle finansiert skoler og sykehus.",categoryId:3},{id:31,text:"Fagforeninger har rett til \xe5 lamme hele samfunn med streiker, selv under kriser n\xe5r menneskeliv st\xe5r p\xe5 spill.",categoryId:3},{id:32,text:"Private selskaper skal fritt f\xe5 profittere p\xe5 syke og desperate mennesker ved \xe5 ta over hele helsevesenet og utdanningssystemet.",categoryId:3},{id:33,text:"D\xf8dsstraff er den eneste rettferdige straffen for de verste forbrytelsene, selv om uskyldige mennesker uunng\xe5elig vil bli henrettet ved feil.",categoryId:4},{id:34,text:"Kriminelle fortjener \xe5 r\xe5tne i fengsel livet ut uten h\xe5p om l\xf8slatelse, uansett hvor mye de angrer eller rehabiliteres.",categoryId:4},{id:35,text:"Isolasjon er et effektivt straffemiddel som b\xf8r brukes hyppig i fengsler, selv n\xe5r det for\xe5rsaker varig psykisk skade og selvmord.",categoryId:4},{id:36,text:"Innsatte er annenrangs borgere som har mistet retten til \xe5 p\xe5virke samfunnet og fortjener aldri \xe5 f\xe5 stemmeretten tilbake.",categoryId:4},{id:37,text:"Ungdomskriminelle skal d\xf8mmes og straffes som voksne for \xe5 vise at handlinger har konsekvenser, uansett alder eller modenhet.",categoryId:4},{id:38,text:"Kriminelle fortjener \xe5 lide for sine handlinger; rehabilitering er bare sl\xf8sing av ressurser p\xe5 mennesker som aldri vil endre seg.",categoryId:4},{id:39,text:"Ikke-voldelige forbrytere skader samfunnet like mye som voldelige og fortjener like strenge straffer.",categoryId:4},{id:40,text:"Gjengangere har bevist at de er uforbederlige og b\xf8r l\xe5ses inne p\xe5 ubestemt tid etter tredje lovbrudd, uansett hvor bagatellmessig.",categoryId:4},{id:41,text:"Dommere b\xf8r ignorere lovbestemte minimumsstraffer n\xe5r det passer dem, og id\xf8mme straffer basert p\xe5 personlige meninger og f\xf8lelser.",categoryId:4},{id:42,text:"Ofre og deres familier b\xf8r bestemme straffeutm\xe5lingen, inkludert fysisk gjengjeldelse hvis de \xf8nsker det.",categoryId:4},{id:43,text:"Demonstranter har rett til \xe5 lamme hele byer og forstyrre tusenvis av uskyldige menneskers liv for \xe5 fremme sin sak.",categoryId:5},{id:44,text:"Aktivister kan fritt bryte loven og for\xe5rsake omfattende skade p\xe5 samfunnet n\xe5r de mener deres sak er viktig nok.",categoryId:5},{id:45,text:"Politiet b\xf8r bruke maksimal makt for \xe5 knuse ulovlige protester, uansett om demonstrantene er fredelige eller ikke.",categoryId:5},{id:46,text:"Selvutnevnte moralske voktere som bryter loven for sin sak skal straffes hardere enn vanlige kriminelle for deres arroganse.",categoryId:5},{id:47,text:"Aktivister som blokkerer veier eller offentlige bygninger viser sin forakt for vanlige mennesker og fortjener den behandlingen de f\xe5r.",categoryId:5},{id:48,text:"Staten har rett til \xe5 forby alle protester som ikke st\xf8tter regjeringens agenda, under p\xe5skudd av 'offentlig sikkerhet'.",categoryId:5},{id:49,text:"Feige demonstranter som skjuler seg bak masker har ingen rett til anonymitet og fortjener \xe5 bli identifisert og uthengt offentlig.",categoryId:5},{id:50,text:"Boikotter er \xf8konomisk terror som \xf8delegger uskyldige arbeideres liv og b\xf8r behandles som kriminelle handlinger.",categoryId:5},{id:51,text:"Individer skal ha absolutt rett til \xe5 definere sin egen seksuelle identitet, selv n\xe5r det undergraver etablerte sosiale normer og skaper kaos.",categoryId:6},{id:52,text:"Kvotering er rettferdig diskriminering som prioriterer gruppeidentitet over individuell kompetanse og hardt arbeid.",categoryId:6},{id:53,text:"Religi\xf8se institusjoner skal fritt kunne diskriminere mot enhver gruppe basert p\xe5 sine tolkninger av gamle tekster.",categoryId:6},{id:54,text:"Samfunnet skal p\xe5legges enorme kostnader for \xe5 tilrettelegge for en liten minoritet med funksjonsnedsettelser, uansett hvor \xf8konomisk uforsvarlig det er.",categoryId:6},{id:55,text:"Enhver kritikk av minoritetsgrupper skal defineres som hatprat og straffes med fengsel, uansett faktagrunnlag eller intensjon.",categoryId:6},{id:56,text:"Religi\xf8se symboler skal forbys fra offentligheten fordi de forn\xe6rmer ikke-troende og utfordrer det sekul\xe6re samfunnet.",categoryId:6},{id:57,text:"Barn ned i barneskolealder skal kunne ta irreversible medisinske beslutninger om kj\xf8nnskorrigerende behandling uten foreldrenes samtykke.",categoryId:6},{id:58,text:"Private bedrifter skal kunne diskriminere og nekte tjenester til alle de er politisk uenige med, uten konsekvenser.",categoryId:6},{id:59,text:"Kj\xf8nnsdelte idrettskonkurranser er arkaiske og diskriminerende og m\xe5 avskaffes for total inkludering uansett biologiske forskjeller.",categoryId:6},{id:60,text:"Anonyme jobbs\xf8knader tvinger arbeidsgivere til \xe5 ansette personer de ellers ville diskriminert mot med god grunn.",categoryId:6},{id:61,text:"Staten har rett til \xe5 overv\xe5ke hver eneste melding, samtale og bevegelse til alle innbyggere for \xe5 beskytte dem mot usynlige trusler.",categoryId:7},{id:62,text:"Teknologiselskaper skal fritt kunne selge og profittere p\xe5 hver eneste detalj av ditt personlige liv uten reelt samtykke.",categoryId:7},{id:63,text:"Enkeltpersoner har rett til \xe5 kreve at all negativ eller pinlig informasjon om dem slettes fra internett, uansett hvor sann eller viktig den er.",categoryId:7},{id:64,text:"Myndighetene b\xf8r installere ansiktsgjenkjenningsteknologi p\xe5 hvert gatehj\xf8rne for \xe5 identifisere og spore alle borgere i sanntid.",categoryId:7},{id:65,text:"Barn fortjener ingen privatliv fra sine foreldre, som har rett til \xe5 overv\xe5ke og kontrollere alle aspekter ved deres liv.",categoryId:7},{id:66,text:"Arbeidsgivere har rett til \xe5 overv\xe5ke ansatte d\xf8gnet rundt, ogs\xe5 hjemme, for \xe5 sikre maksimal produktivitet og lojalitet.",categoryId:7},{id:67,text:"Hemmelige tjenester skal kunne overv\xe5ke enhver person de mistenker uten domstolkontroll, basert p\xe5 vage mistanker eller politiske motiver.",categoryId:7},{id:68,text:"Staten har rett til \xe5 tvinge alle til \xe5 installere sporingsapper og dele helsedata under en pandemi, uansett personvernkonsekvenser.",categoryId:7},{id:69,text:"Bare de med noe \xe5 skjule trenger kryptering; lovlydige borgere b\xf8r akseptere at myndighetene kan lese all deres kommunikasjon.",categoryId:7},{id:70,text:"Forsikringsselskaper b\xf8r kunne nekte dekning til personer basert p\xe5 genetiske predisposisjoner og livsstilsvalg avdekket gjennom omfattende datainnsamling.",categoryId:7},{id:71,text:"Vestlige land har rett til \xe5 invadere enhver nasjon som ikke f\xf8lger deres verdier, under p\xe5skudd av menneskerettigheter.",categoryId:8},{id:72,text:"\xd8konomiske sanksjoner som sulter uskyldige sivile er et akseptabelt pressmiddel for \xe5 tvinge frem regimeendring i andre land.",categoryId:8},{id:73,text:"Internasjonale domstoler dominert av vestlige land har rett til \xe5 straffeforf\xf8lge ledere fra utviklingsland, mens vestlige ledere g\xe5r fri for lignende handlinger.",categoryId:8},{id:74,text:"Det er helt rettferdig at noen f\xe5 mektige land kan blokkere enhver internasjonal beslutning gjennom vetomakt, uansett hva resten av verden mener.",categoryId:8},{id:75,text:"Enhver minoritet som er misforn\xf8yd med sin regjering har rett til \xe5 erkl\xe6re uavhengighet og splitte nasjonen, uavhengig av konsekvensene.",categoryId:8},{id:76,text:"Stater har rett til \xe5 bruke d\xf8delig makt mot ubev\xe6pnede migranter ved grensen, inkludert kvinner og barn, for \xe5 avskrekke andre.",categoryId:8},{id:77,text:"Vestlige land b\xf8r prioritere lange diplomatiske prosesser over rask milit\xe6r intervensjon, selv n\xe5r folkegrupper blir utslettet mens de venter.",categoryId:8},{id:78,text:"Vestlige land har en plikt til \xe5 p\xe5tvinge sitt politiske system p\xe5 andre kulturer, uansett om det er \xf8nsket eller tilpasset lokale forhold.",categoryId:8},{id:79,text:"Enhver dialog med terrorgrupper er en feig ettergivelse som bel\xf8nner vold og oppmuntrer til mer terrorisme.",categoryId:8},{id:80,text:"Urfolk som utgj\xf8r en liten minoritet fortjener \xe5 kontrollere enorme landomr\xe5der basert p\xe5 forfedres tilstedev\xe6relse, p\xe5 bekostning av majoriteten.",categoryId:8},{id:81,text:"Vestlige feminister b\xf8r tvinge sin forst\xe5else av kvinnerettigheter p\xe5 alle kulturer, uansett lokal motstand eller kulturelle tradisjoner.",categoryId:9},{id:82,text:"Kj\xf8nnskvotering gir ufortjente fordeler til mindre kvalifiserte kvinner p\xe5 bekostning av bedre kvalifiserte menn.",categoryId:9},{id:83,text:"Kvinner skal ha ubegrenset rett til abort n\xe5r som helst i svangerskapet, av hvilken som helst grunn, uten samfunnets innblanding.",categoryId:9},{id:84,text:"Religi\xf8se lover som behandler kvinner som underordnet menn er helt akseptable og b\xf8r respekteres av sekul\xe6re samfunn.",categoryId:9},{id:85,text:"Vestlige land har rett til \xe5 p\xe5tvinge sine kulturelle normer p\xe5 andre samfunn og forby tradisjoner de misliker, uansett lokal kontekst.",categoryId:9},{id:86,text:"Prevensjon er en menneskerett som skal v\xe6re gratis og tilgjengelig for alle, selv mindre\xe5rige, uten foreldres kunnskap eller samtykke.",categoryId:9},{id:87,text:"Biologiske forskjeller mellom kj\xf8nnene er fullstendig irrelevante i idrett og alle konkurranser b\xf8r v\xe6re \xe5pne for alle uansett biologisk fordel.",categoryId:9},{id:88,text:"Staten b\xf8r tvinge foreldre til \xe5 dele foreldrepermisjon likt, uavhengig av familiens \xf8nsker, \xf8konomiske situasjon eller den enkeltes karriere.",categoryId:9},{id:89,text:"Myndighetene har rett til \xe5 gripe inn i familier og ekteskap ved den minste mistanke om konflikt, uten bevis eller rettslig prosess.",categoryId:9},{id:90,text:"Enhver klage om seksuell trakassering m\xe5 automatisk antas \xe5 v\xe6re sann, og den anklagede m\xe5 m\xf8te konsekvenser uten mulighet til \xe5 forsvare seg.",categoryId:9},{id:91,text:"Urfolks krav om landrettigheter basert p\xe5 eldgamle tradisjoner skal trumfe moderne samfunns behov for ressurser og utvikling.",categoryId:10},{id:92,text:"Urfolk som utgj\xf8r en liten minoritet av befolkningen skal ha eksklusiv kontroll over enorme ressurser p\xe5 bekostning av resten av landet.",categoryId:10},{id:93,text:"Skoler skal tvinges til \xe5 prioritere urfolks spr\xe5k og kultur over moderne ferdigheter elevene trenger for \xe5 lykkes i dagens samfunn.",categoryId:10},{id:94,text:"Urfolk fortjener spesielle rettigheter og privilegier som ingen andre borgere f\xe5r, basert utelukkende p\xe5 deres etniske bakgrunn.",categoryId:10},{id:95,text:"Dagens skattebetalere skal tvinges til \xe5 betale enorme erstatninger for historiske urettferdigheter de aldri var involvert i.",categoryId:10},{id:96,text:"Urfolk skal ha sitt eget parallelle rettssystem, selv n\xe5r det krenker individuelle rettigheter som resten av samfunnet tar for gitt.",categoryId:10},{id:97,text:"Milj\xf8hensyn er bare unnskyldninger for \xe5 kontrollere urfolk; deres tradisjonelle praksiser skal aldri begrenses uansett konsekvenser for naturen.",categoryId:10},{id:98,text:"Sm\xe5 urfolksgrupper skal kunne blokkere nasjonale infrastrukturprosjekter som ville gavnet millioner av mennesker.",categoryId:10},{id:99,text:"Urfolk har rett til \xe5 monopolisere og profittere p\xe5 sin kulturelle kunnskap, selv n\xe5r den kunne reddet liv eller gavnet menneskeheten.",categoryId:10},{id:100,text:"Moderne rettssystemer skal vike for urfolks tradisjonelle lover, selv n\xe5r disse krenker grunnleggende menneskerettigheter.",categoryId:10}]}}]);